import "./styles.css";
import { useState } from "react";

function Square({value, onSquareClick}) {
    return <button className="square" onClick={onSquareClick}>{value}</button>
}

function Board({ xIsTurn }) {
    const [squares, setSquares] = useState(Array(9).fill(null));
    let winner = calculateWinner(squares);

    let currentTurn = xIsTurn ? "X" : "0"
    let status = winner ?  "Winner is " + winner : "current turn is " + currentTurn;

    function calculateWinner(squares) {
        const winningCon = [
            [0, 1, 2],
            [3, 4, 5],
            [6, 7, 8],
            [0, 3, 6],
            [1, 4, 7],
            [2, 5, 8],
            [0, 4, 8],
            [2, 4, 6]
        ]

        for (let i=0; i < winningCon.length; i++) {
            const [a, b ,c] = winningCon[i];

            if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
                return squares[a];
            }
        }

        return null;
    }

    function handleClick(i) {
        if(squares[i] || calculateWinner(squares)) {
            return;
        }

        let token = '0';
        if (xIsTurn) {
            token = 'X';
        } else {
            token = '0';
        }

        const selectedSquares = squares.slice();
        selectedSquares[i] = token;
        setSquares(selectedSquares);
        setxIsTurn(!xIsTurn);
    }

    function reset() {
        setSquares(Array(9).fill(null));
        setxIsTurn(false);
    }


    return (
        <>
        <div className="status">{status}</div>
        <div className="board-row">
            <Square value={squares[0]} onSquareClick={() => handleClick(0)}/>
            <Square value={squares[1]} onSquareClick={() => handleClick(1)}/>
            <Square value={squares[2]} onSquareClick={() => handleClick(2)}/>
        </div>
        <div className="board-row">
            <Square value={squares[3]} onSquareClick={() => handleClick(3)}/>
            <Square value={squares[4]} onSquareClick={() => handleClick(4)}/>
            <Square value={squares[5]} onSquareClick={() => handleClick(5)}/>
        </div>
        <div className="board-row">
            <Square value={squares[6]} onSquareClick={() => handleClick(6)}/>
            <Square value={squares[7]} onSquareClick={() => handleClick(7)}/>
            <Square value={squares[8]} onSquareClick={() => handleClick(8)}/>
        </div>
        <button onClick={reset}>reset</button>
        </>
    );
  }

  export default function Game() {
    const [xIsTurn, setxIsTurn] = useState(false);
    return (
        <div className="game">
            <div className="game-board">
            <Board xIsTurn={xIsTurn}/>
            </div>
            <div className="game-info">
            <ol>{/*TODO*/}</ol>
            </div>
        </div>
    );
  }
